insert into weather_data (id, "name", description)
values
('2d85f9cc-1c95-4329-aaaf-b969795521f6'::UUID, 'Температура воздуха', 'Одним из основных факторов, влияющих на степень пожароопасности по условиям погоды, является температура воздуха. Известно, что нагреваемый на солнце ЛГМ со временем теряет влагу, вследствие чего увеличивают свой потенциал к горению. Поверхность почвы, нагреваемая на солнце, так же нагревает находящийся рядом воздух, что способствует движению воздушных масс. Нагревание лесных горючих материалов, в результате чего происходит потеря влаги и уменьшается время загорания, способствует увеличению пожароопасности.'),
('01b4a98b-255f-4777-b765-a7d093a1b1d5'::UUID, 'Температура точки росы', 'Точка росы — это температура, при которой водяной пар в воздухе достигает насыщения и начинает конденсироваться в росу. Этот параметр играет важную роль в прогнозировании лесных пожаров, так как он напрямую влияет на влажность воздуха и, следовательно, на вероятность возгораний. Температура точки росы определяется только двумя параметрами: температурой и относительной влажностью воздуха. Чем выше относительная влажность, тем точка росы выше и ближе к фактической температуре воздуха.'),
('8b205197-8761-468a-bdd4-0e0538e68ff3'::UUID, 'Суточные осадки', 'Выпадение осадков способствует уменьшению пожарной опасности, однако из-за задерживающего эффекта крон деревьев, земли достигают не все осадки, а только их часть. Значит, разным типам леса требуется разное количество осадков для насыщения влагой ЛГМ. Так наименьшее количество осадков необходимо сосновому лесу (от 1 мм), а наибольшее – ельникам (15 мм). Однако тут имеет место и обратный механизм испарения: как в тех типах леса, где полное увлажнение происходит быстро, так же быстро происходит и высыхание ЛГМ и наоборот. Следует учитывать и то, что для возникновения и распространения огня в лесу не нужно высыхания всей толщи напочвенного покрова, а для обеспечения невоспламеняемости лесных горючих материалов не требуется его полного смачивания.'),
('735e5d89-5c69-42c5-856b-c0027ddd01a4'::UUID, 'Скорость ветра', 'Чем сильнее ветер, тем быстрее распространяется пожар. Это обусловлено дополнительным притоком кислорода к пламени и его переносом на расположенные спереди горючие материалы, что способствует распространению пожара. Лесной пожар сам по себе вызывает возникновение локальных воздушных потоков из-за разницы температур, что усиливает эффект распространения огня по ветру. Воздух над поверхностью пламени нагревается, он поднимается. Его место занимает свежий воздух, способствующий процессу горения. В результате, над пожаром образуется конвекционная воронка, в которую попадают различные сгораемые материалы (ветки, пучки, хвои), которые поднимаются над лесным пологом, а затем опускаются на расстоянии от 200 до 300 метров. Это всё создаёт новые очаги возгорания.' ),
('d4a0a6bd-5706-4cd5-a677-30695b4890e8'::UUID, 'Влажность воздуха', 'Влажность воздуха характеризует насыщенность атмосферы водяным паром. Количество влаги отражается на влагосодержании лесных горючих материалов. В зависимости от того, какова влажность воздуха, меняется влагосодержание ЛГМ, что влияет на пожароопасность. К проводникам горения относятся те типы лесных горючих материалов, которые вовсе или практически не способны регулировать свою влажность. Такие ЛГМ, как лишайники, опады, лесные подстилки, торф, валежник и т.д. сохраняют влагу только по законам увлажнения и усыхания.');
